import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import FloatingLabel from 'react-bootstrap/FloatingLabel';
import { Container } from 'react-bootstrap';
import { useState } from 'react';

type Props = {
    onDodaj: (nazwa: string, wpis: string) => void;
};

function Wpis({ onDodaj }: Props) {
  const [nazwa, setNazwa] = useState('');
  const [wpis, setWpis] = useState('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (nazwa.trim() && wpis.trim()) {
      onDodaj(nazwa, wpis);
      setNazwa('');
      setWpis('');
    }
  };

    return (
        <Container className='Wpis-custom shadow-sm m-5'>
            <h2>Przyłącz się do dyskusji...</h2>
            <Form onSubmit={handleSubmit}>
            <FloatingLabel
                controlId="floatingInput"
                label="Nazwa użytkownika"
                className="mb-3"
            >
                <Form.Control
                    type="text"
                    placeholder="Twoje imię"
                    value={nazwa}
                    onChange={(e) => setNazwa(e.target.value)}
                />
            </FloatingLabel>
            <FloatingLabel controlId="floatingTextarea2" label="Napisz coś">
                <Form.Control
                    as="textarea"
                    placeholder="Twoja wiadomość"
                    style={{ height: '100px' }}
                    value={wpis}
                    onChange={(e) => setWpis(e.target.value)}
                />
            </FloatingLabel>
            <Button type="submit" variant="primary" className="m-2">Wyślij</Button>
            </Form>
        </Container>
    )
}
export default Wpis;
