import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import FloatingLabel from 'react-bootstrap/FloatingLabel';
import { Container } from 'react-bootstrap';
import { useState } from 'react';

type Props = {
    onDodaj: (nazwa: string, wpis: string) => void;
};

function Wpis({ onDodaj }: Props) {
    const [nazwa, setNazwa] = useState('');
    const [wpis, setWpis] = useState('');

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        if (nazwa.trim() && wpis.trim()) {
            onDodaj(nazwa, wpis);
            setNazwa('');
            setWpis('');
        }
    };

    return (
        <Container className='Wpis-custom shadow-sm m-5 position-relative d-flex justify-content-center align-items-center'>
            <div className="tlo-custom position-absolute top-0 start-0 w-100 h-100"></div>

            {/* Główne wnętrze, flex-kolumna */}
            <div className="position-relative d-flex flex-column w-100 h-100 p-3" style={{ maxWidth: '600px' }}>

                {/* Nagłówek na górze */}
                <h2 className="text-center mb-3">Przyłącz się do dyskusji...</h2>

                {/* Formularz rozciągnięty */}
                <Form onSubmit={handleSubmit} className="d-flex flex-column flex-grow-1 justify-content-between">
                    <div>
                        <FloatingLabel controlId="floatingInput" label="Nazwa użytkownika" className="mb-3">
                            <Form.Control
                                type="text"
                                placeholder="Twoje imię"
                                value={nazwa}
                                onChange={(e) => setNazwa(e.target.value)}
                            />
                        </FloatingLabel>

                        <FloatingLabel controlId="floatingTextarea2" label="Napisz coś">
                            <Form.Control
                                as="textarea"
                                placeholder="Twoja wiadomość"
                                style={{ height: '100px' }}
                                value={wpis}
                                onChange={(e) => setWpis(e.target.value)}
                            />
                        </FloatingLabel>
                    </div>

                    {/* Przycisk na dole */}
                    <Button type="submit" variant="primary" className="button-custom mt-3 align-self-center">
                        Wyślij
                    </Button>
                </Form>
            </div>
        </Container>
    )
}
export default Wpis;
