import './App.css';
import { Container } from 'react-bootstrap';
import { useEffect, useState } from 'react';
import { Forum } from './Forum.ts';
import { ForumItem } from './ForumItem.tsx';
import Wpis from './Wpis.tsx';

function ForumPage() {
  const [wpisy, setWpisy] = useState<Forum[]>([]);

  useEffect(() => {
    const wpisyPobrane: Forum[] = [
      {
        Id: 1,
        Zdjecie: "profile.jpg",
        Nazwa: "Kuroneko",
        Wpis: "Lorem ipsum dolor sit amet..."
      },
      {
        Id: 2,
        Zdjecie: "profile.jpg",
        Nazwa: "Ares",
        Wpis: "Lorem ipsum dolor sit amet..."
      }
    ];
    setWpisy(wpisyPobrane);
  }, []);

  const dodajWpis = (nazwa: string, wpis: string) => {
    const nowyWpis: Forum = {
      Id: wpisy.length + 1,
      Zdjecie: "profile.jpg",
      Nazwa: nazwa,
      Wpis: wpis
    };
    setWpisy([nowyWpis, ...wpisy]); // nowy wpis na górze listy
  };

  return (
    <Container className='m-5'>
      <Wpis onDodaj={dodajWpis} />
      {wpisy.map((item, index) => (
        <ForumItem key={index} forum={item} />
      ))}
    </Container>
  );
}

export default ForumPage;
